definition.script:
pipeline {
    agent {
        docker {
            image 'ubuntu'
            args '-u root:sudo -v $HOME/workspace/myproject:/myproject'
        }
    }
    stages {
        stage("setup_env") {
            steps {
                sh 'bash ./setup_netbox_env.sh'
            }
        }
        stage("docker_start") {
            steps {
                sh 'docker info'
                sh '/etc/init.d/docker start'
            }
        }
        stage("docker-compose_setup") {
            steps {
                sh 'curl -L "https://github.com/docker/compose/releases/download/1.29.2/docker-compose-$(uname -s)-$(uname -m)" -o /usr/local/bin/docker-compose'
                sh 'chmod +x /usr/local/bin/docker-compose'
                sh 'docker-compose --version'
            }
        }
        stage("create_directory") {
            steps {
                sh 'mkdir -p projects && cd projects'
            }
        }

        stage("clone_netbox") {
            steps {
                sh 'git clone -b 2.3.0 https://github.com/netbox-community/netbox-docker.git'
                sh 'cp netbox-docker/docker-compose.override.yml.example netbox-docker/docker-compose.override.yml'
                sh 'yq -i \'.services.netbox.environment.NETBOX_DELETE_LEGACY_DATA = "true"\'  netbox-docker/docker-compose.yml'
                sh 'yq -i \'.services.netbox.environment.SKIP_SUPERUSER = "true"\'  netbox-docker/docker-compose.yml'
                sh 'yq -i \'.services.netbox.restart = "unless-stopped"\' netbox-docker/docker-compose.override.yml'
                sh 'yq -i \'.services.netbox.healthcheck.timeout = "10s"\' netbox-docker/docker-compose.yml'
                sh 'yq -i \'.services.netbox-worker.healthcheck.timeout = "10s"\' netbox-docker/docker-compose.yml'
                sh 'yq -i \'.services.netbox-housekeeping.healthcheck.timeout = "10s"\' netbox-docker/docker-compose.yml'
            }
        }

        stage("docker-compose") {
            steps {
                sh 'pwd'
                sh 'docker-compose -f netbox-docker/docker-compose.yml pull'
                sh 'docker-compose -f netbox-docker/docker-compose.yml up -d'
            }
        }
    }
    post {
        success {
            echo 'Do something when it is successful'
            sh 'rm -r netbox-docker'
        }
        failure {
            echo 'Do something when it is failed'
            sh 'rm -r netbox-docker'
        }
    }
}
